import React from 'react';

const MoveList = ({ moves = [], onMoveClick, currentMoveIndex = -1 }) => {
  const getStatusBadge = (status) => {
    const statusConfig = {
      saved: { color: 'bg-green-100 text-green-800', text: 'Saved' },
      unknown: { color: 'bg-gray-100 text-gray-800', text: 'Unknown' },
      pending: { color: 'bg-yellow-100 text-yellow-800', text: 'Pending' }
    };

    const config = statusConfig[status] || statusConfig.unknown;

    return (
      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${config.color}`}>
        {config.text}
      </span>
    );
  };

  if (moves.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <p className="text-gray-500 text-center text-sm">No moves yet</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
      <h3 className="text-sm font-medium text-gray-700 mb-3">Moves</h3>
      <div className="flex space-x-2 overflow-x-auto pb-2">
        {moves.map((move, index) => (
          <button
            key={index}
            onClick={() => onMoveClick && onMoveClick(index)}
            className={`
              flex-shrink-0 px-3 py-2 rounded-lg text-sm font-medium
              transition-all duration-200 whitespace-nowrap
              ${currentMoveIndex === index
                ? 'bg-blue-500 text-white shadow-md'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }
            `}
          >
            <div className="flex flex-col items-center space-y-1">
              <span>{move.notation}</span>
              {getStatusBadge(move.status)}
            </div>
          </button>
        ))}
      </div>
    </div>
  );
};

export default MoveList;
