import React from 'react';

const MoveList = ({ moves = [], onMoveClick, currentMoveIndex = -1 }) => {
  const getStatusBadge = (status) => {
    const statusConfig = {
      saved: { color: 'border-green-300 text-green-600', text: 'Saved' },
      unknown: { color: 'border-gray-300 text-gray-500', text: 'Unknown' },
      pending: { color: 'border-yellow-300 text-yellow-600', text: 'Pending' }
    };

    const config = statusConfig[status] || statusConfig.unknown;

    return (
      <span className={`inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium border ${config.color}`}>
        {config.text}
      </span>
    );
  };

  if (moves.length === 0) {
    return (
      <div className="h-full flex items-center justify-center">
        <div className="text-center">
          <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mb-3">
            <span className="text-gray-400 text-lg">â™Ÿ</span>
          </div>
          <p className="text-gray-400 text-sm">No moves yet</p>
          <p className="text-gray-300 text-xs mt-1">Start playing to see your moves</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-sm font-medium text-gray-700">Move History</h3>
        <span className="text-xs text-gray-400">{moves.length} moves</span>
      </div>

      <div className="flex-1 overflow-y-auto">
        <div className="space-y-2">
          {moves.map((move, index) => (
            <button
              key={index}
              onClick={() => onMoveClick && onMoveClick(index)}
              className={`
                w-full flex items-center justify-between px-3 py-2 text-sm font-medium
                transition-colors duration-200 border rounded-md
                ${currentMoveIndex === index
                  ? 'border-blue-500 text-blue-600 bg-blue-50'
                  : 'border-gray-200 text-gray-600 hover:border-gray-300 hover:text-gray-700 hover:bg-gray-50'
                }
              `}
            >
              <div className="flex items-center space-x-2">
                <span className="text-xs text-gray-400 font-mono">#{index + 1}</span>
                <span className="font-medium">{move.notation}</span>
              </div>
              {getStatusBadge(move.status)}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MoveList;
