import React, { useState } from 'react';
import ChessBoard from '../components/ChessBoard';

const Training = () => {
  const [pieces, setPieces] = useState({});
  const [selectedSquare, setSelectedSquare] = useState(null);
  const [movesRemaining, setMovesRemaining] = useState(15);
  const [trainingSuccess, setTrainingSuccess] = useState(null); // null, true, false

  const handleSquareClick = (file, rank, square) => {
    if (selectedSquare) {
      // Make a move
      const newPieces = { ...pieces };
      if (pieces[selectedSquare]) {
        newPieces[square] = pieces[selectedSquare];
        delete newPieces[selectedSquare];
      }
      setPieces(newPieces);

      // Decrease moves remaining
      setMovesRemaining(prev => Math.max(0, prev - 1));

      // Simulate training result (random for demo)
      if (movesRemaining <= 1) {
        const success = Math.random() > 0.5;
        setTrainingSuccess(success);
      }

      setSelectedSquare(null);
    } else {
      setSelectedSquare(square);
    }
  };

  const getSuccessIndicator = () => {
    if (trainingSuccess === null) {
      return (
        <div className="flex items-center space-x-2">
          <div className="w-3 h-3 bg-gray-400 rounded-full animate-pulse"></div>
          <span className="text-gray-600 text-sm">Training in progress...</span>
        </div>
      );
    }

    if (trainingSuccess) {
      return (
        <div className="flex items-center space-x-2">
          <div className="w-6 h-6 bg-green-500 rounded-full flex items-center justify-center">
            <span className="text-white text-sm">✓</span>
          </div>
          <span className="text-green-700 text-sm font-medium">Success!</span>
        </div>
      );
    }

    return (
      <div className="flex items-center space-x-2">
        <div className="w-6 h-6 bg-red-500 rounded-full flex items-center justify-center">
          <span className="text-white text-sm">✗</span>
        </div>
        <span className="text-red-700 text-sm font-medium">Try again</span>
      </div>
    );
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Chess Board */}
        <div>
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h2 className="text-lg font-semibold text-gray-800 mb-4">Training Board</h2>
            <div className="flex justify-center">
              <ChessBoard
                onSquareClick={handleSquareClick}
                pieces={pieces}
                selectedSquare={selectedSquare}
              />
            </div>
          </div>
        </div>

        {/* Training Dashboard */}
        <div className="space-y-6">
          {/* Moves Counter */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Training Progress</h3>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-gray-600">Moves remaining today:</span>
                <span className="text-2xl font-bold text-blue-600">{movesRemaining}</span>
              </div>

              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${((15 - movesRemaining) / 15) * 100}%` }}
                ></div>
              </div>
            </div>
          </div>

          {/* Success/Failure Indicator */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Status</h3>
            {getSuccessIndicator()}
          </div>

          {/* Training Tips */}
          <div className="bg-blue-50 rounded-lg border border-blue-200 p-4">
            <h4 className="text-sm font-medium text-blue-800 mb-2">Training Tips</h4>
            <ul className="text-sm text-blue-700 space-y-1">
              <li>• Focus on one move at a time</li>
              <li>• Practice regularly for best results</li>
              <li>• Review your moves after each session</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Training;
