import React from 'react';

const MoveList = ({ moves = [], onMoveClick, currentMoveIndex = -1 }) => {
  const getStatusBadge = (status) => {
    const statusConfig = {
      saved: { color: 'bg-green-100 text-green-800', text: 'Saved' },
      unknown: { color: 'bg-gray-100 text-gray-800', text: 'Unknown' },
      pending: { color: 'bg-yellow-100 text-yellow-800', text: 'Pending' }
    };

    const config = statusConfig[status] || statusConfig.unknown;

    return (
      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${config.color}`}>
        {config.text}
      </span>
    );
  };

  if (moves.length === 0) {
    return (
      <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-6">
        <div className="text-center">
          <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-2xl">‚ôüÔ∏è</span>
          </div>
          <p className="text-gray-500 text-sm font-medium">No moves yet</p>
          <p className="text-gray-400 text-xs mt-1">Start playing to see your moves here</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-800">Move History</h3>
        <div className="w-8 h-8 bg-gradient-to-r from-green-500 to-green-600 rounded-lg flex items-center justify-center">
          <span className="text-white text-sm font-bold">üìù</span>
        </div>
      </div>
      <div className="flex space-x-3 overflow-x-auto pb-4">
        {moves.map((move, index) => (
          <button
            key={index}
            onClick={() => onMoveClick && onMoveClick(index)}
            className={`
              flex-shrink-0 px-4 py-3 rounded-xl text-sm font-semibold
              transition-all duration-300 transform hover:scale-105 whitespace-nowrap
              ${currentMoveIndex === index
                ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg'
                : 'bg-gray-50 text-gray-700 hover:bg-gray-100 border border-gray-200'
              }
            `}
          >
            <div className="flex flex-col items-center space-y-2">
              <span className="text-base">{move.notation}</span>
              {getStatusBadge(move.status)}
            </div>
          </button>
        ))}
      </div>
    </div>
  );
};

export default MoveList;
