import React, { useState } from 'react';

const Settings = () => {
  const [successDateFactor, setSuccessDateFactor] = useState(1.5);
  const [delayBeforeNextMove, setDelayBeforeNextMove] = useState(2000);
  const [theme, setTheme] = useState('system');
  const [syncStatus, setSyncStatus] = useState(false);
  const [lastLocalUpdate, setLastLocalUpdate] = useState('2024-01-15 14:30:22');
  const [lastRemoteUpdate, setLastRemoteUpdate] = useState('2024-01-15 12:15:45');

  const handleResetToDefault = () => {
    setSuccessDateFactor(1.5);
    setDelayBeforeNextMove(2000);
    setTheme('system');
  };

  const handleEraseAllData = () => {
    if (window.confirm('Are you sure you want to erase all data? This action cannot be undone.')) {
      // Reset all state
      setSuccessDateFactor(1.5);
      setDelayBeforeNextMove(2000);
      setTheme('system');
      setSyncStatus(false);
      setLastLocalUpdate('Never');
      setLastRemoteUpdate('Never');
    }
  };

  const handleUploadLocal = () => {
    setSyncStatus(true);
    setLastRemoteUpdate(new Date().toLocaleString());
  };

  const handleDownloadRemote = () => {
    setSyncStatus(true);
    setLastLocalUpdate(new Date().toLocaleString());
  };

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
      <div className="space-y-6">
        {/* Sliders Section */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-6">Training Settings</h2>

          <div className="space-y-6">
            {/* Success Date Factor Slider */}
            <div>
              <div className="flex justify-between items-center mb-2">
                <label className="text-sm font-medium text-gray-700">
                  On Success Date Factor
                </label>
                <span className="text-sm text-gray-500">{successDateFactor}</span>
              </div>
              <input
                type="range"
                min="0.5"
                max="3"
                step="0.1"
                value={successDateFactor}
                onChange={(e) => setSuccessDateFactor(parseFloat(e.target.value))}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
              />
              <div className="flex justify-between text-xs text-gray-400 mt-1">
                <span>0.5</span>
                <span>1.5</span>
                <span>3.0</span>
              </div>
            </div>

            {/* Delay Before Next Move Slider */}
            <div>
              <div className="flex justify-between items-center mb-2">
                <label className="text-sm font-medium text-gray-700">
                  Delay Before Next Move
                </label>
                <span className="text-sm text-gray-500">{delayBeforeNextMove}ms</span>
              </div>
              <input
                type="range"
                min="500"
                max="5000"
                step="100"
                value={delayBeforeNextMove}
                onChange={(e) => setDelayBeforeNextMove(parseInt(e.target.value))}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
              />
              <div className="flex justify-between text-xs text-gray-400 mt-1">
                <span>500ms</span>
                <span>2000ms</span>
                <span>5000ms</span>
              </div>
            </div>
          </div>
        </div>

        {/* Theme Section */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Theme</h3>
          <div className="grid grid-cols-3 gap-3">
            {['system', 'light', 'dark'].map((themeOption) => (
              <button
                key={themeOption}
                onClick={() => setTheme(themeOption)}
                className={`
                  px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200
                  ${theme === themeOption
                    ? 'bg-blue-500 text-white shadow-md'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }
                `}
              >
                {themeOption.charAt(0).toUpperCase() + themeOption.slice(1)}
              </button>
            ))}
          </div>
        </div>

        {/* Data Management Section */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Data Management</h3>
          <div className="space-y-3">
            <button
              onClick={handleResetToDefault}
              className="w-full px-4 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors duration-200 text-sm font-medium"
            >
              Reset to Default
            </button>
            <button
              onClick={handleEraseAllData}
              className="w-full px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors duration-200 text-sm font-medium"
            >
              Erase All Data
            </button>
          </div>
        </div>

        {/* Sync Status Section */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Sync Status</h3>

          <div className="space-y-4">
            {/* Status Indicators */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Local last update:</span>
                  <span className="text-sm font-medium text-gray-800">{lastLocalUpdate}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Remote last update:</span>
                  <span className="text-sm font-medium text-gray-800">{lastRemoteUpdate}</span>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <div className={`w-3 h-3 rounded-full ${syncStatus ? 'bg-green-500' : 'bg-red-500'}`}></div>
                <span className={`text-sm font-medium ${syncStatus ? 'text-green-700' : 'text-red-700'}`}>
                  {syncStatus ? 'Databases synced' : 'Databases NOT synced'}
                </span>
              </div>
            </div>

            {/* Sync Buttons */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              <button
                onClick={handleUploadLocal}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200 text-sm font-medium"
              >
                Upload Local → Remote
              </button>
              <button
                onClick={handleDownloadRemote}
                className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors duration-200 text-sm font-medium"
              >
                Download Remote → Local
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
