import React, { useState } from 'react';
import ChessBoard from '../components/ChessBoard';
import MoveList from '../components/MoveList';

const Board = () => {
  const [pieces, setPieces] = useState({});
  const [selectedSquare, setSelectedSquare] = useState(null);
  const [moves, setMoves] = useState([]);
  const [currentMoveIndex, setCurrentMoveIndex] = useState(-1);
  const [boardRotated, setBoardRotated] = useState(false);

  const handleSquareClick = (file, rank, square) => {
    if (selectedSquare) {
      // Make a move
      const newMove = {
        from: selectedSquare,
        to: square,
        notation: `${selectedSquare}-${square}`,
        status: 'unknown'
      };

      const newMoves = [...moves, newMove];
      setMoves(newMoves);
      setCurrentMoveIndex(newMoves.length - 1);

      // Update pieces (simple move)
      const newPieces = { ...pieces };
      if (pieces[selectedSquare]) {
        newPieces[square] = pieces[selectedSquare];
        delete newPieces[selectedSquare];
      }
      setPieces(newPieces);

      setSelectedSquare(null);
    } else {
      // Select a square
      setSelectedSquare(square);
    }
  };

  const handleBack = () => {
    if (currentMoveIndex > 0) {
      setCurrentMoveIndex(currentMoveIndex - 1);
    }
  };

  const handleForward = () => {
    if (currentMoveIndex < moves.length - 1) {
      setCurrentMoveIndex(currentMoveIndex + 1);
    }
  };

  const handleReset = () => {
    setPieces({});
    setMoves([]);
    setCurrentMoveIndex(-1);
    setSelectedSquare(null);
  };

  const handleRotateBoard = () => {
    setBoardRotated(!boardRotated);
  };

  const handleSave = () => {
    if (moves.length > 0) {
      const updatedMoves = moves.map(move => ({
        ...move,
        status: 'saved'
      }));
      setMoves(updatedMoves);
    }
  };

  const handleDelete = () => {
    if (moves.length > 0) {
      const newMoves = moves.slice(0, -1);
      setMoves(newMoves);
      setCurrentMoveIndex(Math.max(-1, newMoves.length - 1));
    }
  };

  const handleMoveClick = (index) => {
    setCurrentMoveIndex(index);
  };

  const controlButtons = [
    { icon: '‚Üê', label: 'Back', onClick: handleBack, disabled: currentMoveIndex <= 0 },
    { icon: '‚Üí', label: 'Forward', onClick: handleForward, disabled: currentMoveIndex >= moves.length - 1 },
    { icon: '‚è™', label: 'Reset', onClick: handleReset },
    { icon: '‚ü≥', label: 'Rotate', onClick: handleRotateBoard },
    { icon: 'üíæ', label: 'Save', onClick: handleSave, disabled: moves.length === 0 },
    { icon: 'üóë', label: 'Delete', onClick: handleDelete, disabled: moves.length === 0 }
  ];

  return (
    <div className="h-screen flex flex-col max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
      {/* Header - Compact */}
      <div className="mb-4">
        <h1 className="text-xl font-light text-gray-900 mb-1">
          Chess Board
          <div className="w-10 h-0.5 bg-blue-500 mt-1"></div>
        </h1>
        <p className="text-gray-500 text-xs">Practice and analyze your moves</p>
      </div>

      <div className="flex-1 grid grid-cols-1 lg:grid-cols-4 gap-4 min-h-0">
        {/* Chess Board - Center */}
        <div className="lg:col-span-3 flex flex-col">
          <div className="flex-1 flex items-center justify-center">
            <div className={`transform transition-transform duration-300 ${boardRotated ? 'rotate-180' : ''}`}>
              <ChessBoard
                onSquareClick={handleSquareClick}
                pieces={pieces}
                selectedSquare={selectedSquare}
              />
            </div>
          </div>

          {/* Control Toolbar - Compact */}
          <div className="mt-4 flex justify-center">
            <div className="flex items-center space-x-1 bg-gray-50 rounded-lg p-1 border border-gray-200">
              {controlButtons.map((button, index) => (
                <button
                  key={index}
                  onClick={button.onClick}
                  disabled={button.disabled}
                  className={`
                    flex items-center space-x-1 px-3 py-1.5 text-xs font-medium rounded-md transition-all duration-200
                    ${button.disabled
                      ? 'text-gray-400 cursor-not-allowed bg-gray-100'
                      : 'text-gray-700 hover:text-gray-900 hover:bg-white hover:shadow-sm border border-transparent hover:border-gray-200'
                    }
                  `}
                  title={button.label}
                >
                  <span className="text-sm">{button.icon}</span>
                  <span className="hidden lg:inline">{button.label}</span>
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Move List - Right Side */}
        <div className="lg:col-span-1 flex flex-col">
          <div className="flex-1 min-h-0">
            <MoveList
              moves={moves}
              onMoveClick={handleMoveClick}
              currentMoveIndex={currentMoveIndex}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Board;
