import React, { useState } from 'react';
import ChessBoard from '../components/ChessBoard';
import MoveList from '../components/MoveList';

const Board = () => {
  const [pieces, setPieces] = useState({});
  const [selectedSquare, setSelectedSquare] = useState(null);
  const [moves, setMoves] = useState([]);
  const [currentMoveIndex, setCurrentMoveIndex] = useState(-1);
  const [moveHistory, setMoveHistory] = useState([]);
  const [boardRotated, setBoardRotated] = useState(false);

  const handleSquareClick = (file, rank, square) => {
    if (selectedSquare) {
      // Make a move
      const newMove = {
        from: selectedSquare,
        to: square,
        notation: `${selectedSquare}-${square}`,
        status: 'unknown'
      };

      const newMoves = [...moves, newMove];
      setMoves(newMoves);
      setCurrentMoveIndex(newMoves.length - 1);

      // Update pieces (simple move)
      const newPieces = { ...pieces };
      if (pieces[selectedSquare]) {
        newPieces[square] = pieces[selectedSquare];
        delete newPieces[selectedSquare];
      }
      setPieces(newPieces);

      setSelectedSquare(null);
    } else {
      // Select a square
      setSelectedSquare(square);
    }
  };

  const handleBack = () => {
    if (currentMoveIndex > 0) {
      setCurrentMoveIndex(currentMoveIndex - 1);
    }
  };

  const handleForward = () => {
    if (currentMoveIndex < moves.length - 1) {
      setCurrentMoveIndex(currentMoveIndex + 1);
    }
  };

  const handleReset = () => {
    setPieces({});
    setMoves([]);
    setCurrentMoveIndex(-1);
    setSelectedSquare(null);
  };

  const handleRotateBoard = () => {
    setBoardRotated(!boardRotated);
  };

  const handleSave = () => {
    if (moves.length > 0) {
      const updatedMoves = moves.map(move => ({
        ...move,
        status: 'saved'
      }));
      setMoves(updatedMoves);
    }
  };

  const handleDelete = () => {
    if (moves.length > 0) {
      const newMoves = moves.slice(0, -1);
      setMoves(newMoves);
      setCurrentMoveIndex(Math.max(-1, newMoves.length - 1));
    }
  };

  const handleMoveClick = (index) => {
    setCurrentMoveIndex(index);
  };

  const controlButtons = [
    { icon: '‚Üê', label: 'Back', onClick: handleBack, disabled: currentMoveIndex <= 0 },
    { icon: '‚Üí', label: 'Forward', onClick: handleForward, disabled: currentMoveIndex >= moves.length - 1 },
    { icon: '‚è™', label: 'Reset', onClick: handleReset },
    { icon: 'üîÑ', label: 'Rotate', onClick: handleRotateBoard },
    { icon: 'üíæ', label: 'Save', onClick: handleSave, disabled: moves.length === 0 },
    { icon: 'üóëÔ∏è', label: 'Delete', onClick: handleDelete, disabled: moves.length === 0 }
  ];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-2">
          Chess Board
        </h1>
        <p className="text-gray-600 text-lg">Practice and analyze your moves</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Chess Board */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-8 hover:shadow-2xl transition-all duration-300">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-gray-800">Game Board</h2>
              <div className="flex items-center space-x-2">
                <span className="text-sm text-gray-500">Moves: {moves.length}</span>
                <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              </div>
            </div>
            <div className="flex justify-center">
              <div className={`transform transition-transform duration-500 ${boardRotated ? 'rotate-180' : ''}`}>
                <ChessBoard
                  onSquareClick={handleSquareClick}
                  pieces={pieces}
                  selectedSquare={selectedSquare}
                />
              </div>
            </div>
          </div>
        </div>

        {/* Controls and Moves */}
        <div className="space-y-6">
          {/* Control Buttons */}
          <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-800">Controls</h3>
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-blue-600 rounded-lg flex items-center justify-center">
                <span className="text-white text-sm font-bold">üéÆ</span>
              </div>
            </div>
            <div className="grid grid-cols-2 gap-3">
              {controlButtons.map((button, index) => (
                <button
                  key={index}
                  onClick={button.onClick}
                  disabled={button.disabled}
                  className={`
                    flex items-center justify-center px-4 py-3 rounded-xl text-sm font-semibold
                    transition-all duration-300 transform hover:scale-105
                    ${button.disabled
                      ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                      : 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg hover:shadow-xl'
                    }
                  `}
                >
                  <span className="mr-2 text-lg">{button.icon}</span>
                  <span className="hidden sm:inline">{button.label}</span>
                </button>
              ))}
            </div>
          </div>

          {/* Move List */}
          <MoveList
            moves={moves}
            onMoveClick={handleMoveClick}
            currentMoveIndex={currentMoveIndex}
          />
        </div>
      </div>
    </div>
  );
};

export default Board;
