import React, { useState } from 'react';

const Settings = () => {
  const [successDateFactor, setSuccessDateFactor] = useState(1.5);
  const [delayBeforeNextMove, setDelayBeforeNextMove] = useState(2000);
  const [theme, setTheme] = useState('system');
  const [syncStatus, setSyncStatus] = useState(false);
  const [lastLocalUpdate, setLastLocalUpdate] = useState('2024-01-15 14:30:22');
  const [lastRemoteUpdate, setLastRemoteUpdate] = useState('2024-01-15 12:15:45');

  const handleResetToDefault = () => {
    setSuccessDateFactor(1.5);
    setDelayBeforeNextMove(2000);
    setTheme('system');
  };

  const handleEraseAllData = () => {
    if (window.confirm('Are you sure you want to erase all data? This action cannot be undone.')) {
      // Reset all state
      setSuccessDateFactor(1.5);
      setDelayBeforeNextMove(2000);
      setTheme('system');
      setSyncStatus(false);
      setLastLocalUpdate('Never');
      setLastRemoteUpdate('Never');
    }
  };

  const handleUploadLocal = () => {
    setSyncStatus(true);
    setLastRemoteUpdate(new Date().toLocaleString());
  };

  const handleDownloadRemote = () => {
    setSyncStatus(true);
    setLastLocalUpdate(new Date().toLocaleString());
  };

  return (
    <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-2">
          Settings
        </h1>
        <p className="text-gray-600 text-lg">Configure your training preferences</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Left Column */}
        <div className="space-y-6">
          {/* Sliders Section */}
          <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-8">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-gray-800">Training Settings</h2>
              <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-blue-600 rounded-xl flex items-center justify-center">
                <span className="text-white text-lg font-bold">‚öôÔ∏è</span>
              </div>
            </div>

            <div className="space-y-8">
              {/* Success Date Factor Slider */}
              <div>
                <div className="flex justify-between items-center mb-4">
                  <label className="text-lg font-semibold text-gray-700">
                    On Success Date Factor
                  </label>
                  <span className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                    {successDateFactor}
                  </span>
                </div>
                <input
                  type="range"
                  min="0.5"
                  max="3"
                  step="0.1"
                  value={successDateFactor}
                  onChange={(e) => setSuccessDateFactor(parseFloat(e.target.value))}
                  className="w-full h-3 bg-gray-200 rounded-xl appearance-none cursor-pointer slider"
                />
                <div className="flex justify-between text-sm text-gray-500 mt-2">
                  <span>0.5</span>
                  <span>1.5</span>
                  <span>3.0</span>
                </div>
              </div>

              {/* Delay Before Next Move Slider */}
              <div>
                <div className="flex justify-between items-center mb-4">
                  <label className="text-lg font-semibold text-gray-700">
                    Delay Before Next Move
                  </label>
                  <span className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                    {delayBeforeNextMove}ms
                  </span>
                </div>
                <input
                  type="range"
                  min="500"
                  max="5000"
                  step="100"
                  value={delayBeforeNextMove}
                  onChange={(e) => setDelayBeforeNextMove(parseInt(e.target.value))}
                  className="w-full h-3 bg-gray-200 rounded-xl appearance-none cursor-pointer slider"
                />
                <div className="flex justify-between text-sm text-gray-500 mt-2">
                  <span>500ms</span>
                  <span>2000ms</span>
                  <span>5000ms</span>
                </div>
              </div>
            </div>
          </div>

          {/* Theme Section */}
          <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-8">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-2xl font-bold text-gray-800">Theme</h3>
              <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl flex items-center justify-center">
                <span className="text-white text-lg font-bold">üé®</span>
              </div>
            </div>
            <div className="grid grid-cols-3 gap-4">
              {['system', 'light', 'dark'].map((themeOption) => (
                <button
                  key={themeOption}
                  onClick={() => setTheme(themeOption)}
                  className={`
                    px-6 py-4 rounded-xl text-base font-semibold transition-all duration-300 transform hover:scale-105
                    ${theme === themeOption
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg'
                      : 'bg-gray-50 text-gray-700 hover:bg-gray-100 border border-gray-200'
                    }
                  `}
                >
                  {themeOption.charAt(0).toUpperCase() + themeOption.slice(1)}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Right Column */}
        <div className="space-y-6">
          {/* Data Management Section */}
          <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-8">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-2xl font-bold text-gray-800">Data Management</h3>
              <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-green-600 rounded-xl flex items-center justify-center">
                <span className="text-white text-lg font-bold">üíæ</span>
              </div>
            </div>
            <div className="space-y-4">
              <button
                onClick={handleResetToDefault}
                className="w-full px-6 py-4 bg-gray-50 text-gray-700 rounded-xl hover:bg-gray-100 transition-all duration-300 text-base font-semibold border border-gray-200 hover:shadow-lg"
              >
                Reset to Default
              </button>
              <button
                onClick={handleEraseAllData}
                className="w-full px-6 py-4 bg-gradient-to-r from-red-500 to-red-600 text-white rounded-xl hover:from-red-600 hover:to-red-700 transition-all duration-300 text-base font-semibold shadow-lg hover:shadow-xl"
              >
                Erase All Data
              </button>
            </div>
          </div>

          {/* Sync Status Section */}
          <div className="bg-white rounded-2xl shadow-xl border border-gray-100 p-8">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-2xl font-bold text-gray-800">Sync Status</h3>
              <div className="w-10 h-10 bg-gradient-to-r from-orange-500 to-orange-600 rounded-xl flex items-center justify-center">
                <span className="text-white text-lg font-bold">üîÑ</span>
              </div>
            </div>

            <div className="space-y-6">
              {/* Status Indicators */}
              <div className="space-y-4">
                <div className="flex items-center justify-between p-4 bg-gray-50 rounded-xl">
                  <span className="text-gray-600 font-medium">Local last update:</span>
                  <span className="font-semibold text-gray-800">{lastLocalUpdate}</span>
                </div>
                <div className="flex items-center justify-between p-4 bg-gray-50 rounded-xl">
                  <span className="text-gray-600 font-medium">Remote last update:</span>
                  <span className="font-semibold text-gray-800">{lastRemoteUpdate}</span>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-4 bg-gray-50 rounded-xl">
                <div className={`w-4 h-4 rounded-full ${syncStatus ? 'bg-green-500' : 'bg-red-500'}`}></div>
                <span className={`font-semibold ${syncStatus ? 'text-green-700' : 'text-red-700'}`}>
                  {syncStatus ? 'Databases synced' : 'Databases NOT synced'}
                </span>
              </div>

              {/* Sync Buttons */}
              <div className="grid grid-cols-1 gap-4">
                <button
                  onClick={handleUploadLocal}
                  className="px-6 py-4 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-xl hover:from-blue-600 hover:to-blue-700 transition-all duration-300 text-base font-semibold shadow-lg hover:shadow-xl"
                >
                  Upload Local ‚Üí Remote
                </button>
                <button
                  onClick={handleDownloadRemote}
                  className="px-6 py-4 bg-gradient-to-r from-green-500 to-green-600 text-white rounded-xl hover:from-green-600 hover:to-green-700 transition-all duration-300 text-base font-semibold shadow-lg hover:shadow-xl"
                >
                  Download Remote ‚Üí Local
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
